package main;

public class GraterThan {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=14;
		int y=5;
		int a=10;
		int b=15;
		
		System.out.println(x>y);
		
		System.out.println(a & b);
		
		x=x<<2;
		System.out.println(x);
		
		x=x>>2;
		System.out.println(x);
		
		
	}
}